configuration.expression:
{
  "repository": {
    "name": steps.repo_data.body.full_name,
    "description": steps.repo_data.body.description,
    "stars": steps.repo_data.body.stargazers_count,
    "forks": steps.repo_data.body.forks_count,
    "open_issues": steps.repo_data.body.open_issues_count,
    "language": steps.repo_data.body.language,
    "last_push": steps.repo_data.body.pushed_at
  },
  "recent_issues": $map(steps.issues_data.body[0..2], function($issue) {
    {
      "title": $issue.title,
      "state": $issue.state,
      "created": $issue.created_at,
      "updated": $issue.updated_at,
      "author": $issue.user.login
    }
  }),
  "latest_releases": $map(steps.releases_data.body[0..1], function($release) {
    {
      "name": $release.name,
      "tag": $release.tag_name,
      "published": $release.published_at,
      "author": $release.author.login
    }
  }),
  "activity_score": steps.repo_data.body.open_issues_count + $count(steps.issues_data.body) + $count(steps.releases_data.body),
  "timestamp": $now()
}

{
  "high_activity": steps.repo_summary.activity_score > vars.activity_threshold,
  "alert_message": steps.repo_summary.activity_score > vars.activity_threshold ? "High activity detected in " & steps.repo_summary.repository.name : "Normal activity level"
}

configuration.body:
{
  "service": "github-monitor",
  "timestamp": steps.repo_summary.timestamp,
  "repository": steps.repo_summary.repository,
  "recent_activity": {
    "issues": steps.repo_summary.recent_issues,
    "releases": steps.repo_summary.latest_releases,
    "activity_score": steps.repo_summary.activity_score,
    "high_activity_alert": steps.activity_check.high_activity
  },
  "alert_message": steps.activity_check.alert_message
}
